  <span class="hljs-keyword">if</span> (<span class="hljs-title class_">Array</span>.<span class="hljs-title function_">isArray</span>(breadcrumbs)) {
    <span class="hljs-keyword">return</span> breadcrumbs.<span class="hljs-title function_">reduce</span>(<span class="hljs-function">(<span class="hljs-params">title, breadcrumb, i</span>) =&gt;</span> {
      <span class="hljs-keyword">if</span> (i === <span class="hljs-number">0</span>) <span class="hljs-keyword">return</span> <span class="hljs-string">`<span class="hljs-subst">${breadcrumb.label}</span>`</span>;
      <span class="hljs-keyword">return</span> <span class="hljs-string">`<span class="hljs-subst">${title}</span> &gt; <span class="hljs-subst">${breadcrumb.label}</span>`</span>;
    }, <span class="hljs-string">&#x27;&#x27;</span>);
