<span class="hljs-comment">// app.listen(process.env.PAYLOAD_PUBLIC_INTERNAL_SERVER_URL.split(&#x27;:&#x27;)[2]);</span>

<span class="hljs-comment">// create an http server (or https if you aren&#x27;t using a reverse proxy)</span>
<span class="hljs-keyword">const</span> server = http.<span class="hljs-title function_">createServer</span>(app)
server.<span class="hljs-title function_">listen</span>(process.<span class="hljs-property">env</span>.<span class="hljs-property">PAYLOAD_PUBLIC_INTERNAL_SERVER_URL</span>.<span class="hljs-title function_">split</span>(<span class="hljs-string">&#x27;:&#x27;</span>)[<span class="hljs-number">2</span>], <span class="hljs-function">() =&gt;</span> {
  <span class="hljs-variable language_">console</span>.<span class="hljs-title function_">log</span>(<span class="hljs-string">`HTTP Server running on port <span class="hljs-subst">${process.env.PAYLOAD_PUBLIC_INTERNAL_SERVER_URL.split(<span class="hljs-string">&#x27;:&#x27;</span>)[<span class="hljs-number">2</span>]}</span>`</span>);
});

<span class="hljs-comment">// Socket.io</span>
<span class="hljs-comment">// Now pass the server instance to socket io, to create a websocket server on the same port</span>
<span class="hljs-keyword">const</span> io = <span class="hljs-built_in">require</span>(<span class="hljs-string">&#x27;socket.io&#x27;</span>)(server, {
