    <span class="hljs-attr">origin</span>: <span class="hljs-variable constant_">WEB_URL</span>,
    <span class="hljs-attr">credentials</span>: <span class="hljs-literal">true</span>,
  },
  <span class="hljs-attr">allowRequest</span>: <span class="hljs-function">(<span class="hljs-params">req, callback</span>) =&gt;</span> {
    <span class="hljs-keyword">const</span> cookies = <span class="hljs-title class_">Object</span>.<span class="hljs-title function_">fromEntries</span>(
      req.<span class="hljs-property">headers</span>.<span class="hljs-property">cookie</span>
        ?.<span class="hljs-title function_">split</span>(<span class="hljs-string">&quot;; &quot;</span>)
        .<span class="hljs-title function_">map</span>(<span class="hljs-function">(<span class="hljs-params">cookie</span>) =&gt;</span> cookie.<span class="hljs-title function_">split</span>(<span class="hljs-string">&quot;=&quot;</span>))
        .<span class="hljs-title function_">filter</span>(<span class="hljs-function">(<span class="hljs-params">tuple</span>) =&gt;</span> tuple.<span class="hljs-property">length</span> === <span class="hljs-number">2</span>) ?? []
    );
    <span class="hljs-keyword">const</span> payloadToken = cookies[<span class="hljs-string">&quot;payload-token&quot;</span>];
    <span class="hljs-keyword">if</span> (<span class="hljs-keyword">typeof</span> payloadToken !== <span class="hljs-string">&quot;string&quot;</span>) {
      <span class="hljs-title function_">callback</span>(<span class="hljs-string">&quot;Not signed in&quot;</span>, <span class="hljs-literal">false</span>);
      <span class="hljs-keyword">return</span>;
    }
    jwt.<span class="hljs-title function_">verify</span>(payloadToken, payload.<span class="hljs-property">secret</span>, <span class="hljs-function">(<span class="hljs-params">err</span>) =&gt;</span> {
      <span class="hljs-keyword">if</span> (err) {
        <span class="hljs-title function_">callback</span>(<span class="hljs-string">&quot;Invalid token&quot;</span>, <span class="hljs-literal">false</span>);
        <span class="hljs-keyword">return</span>;
      }
      <span class="hljs-title function_">callback</span>(<span class="hljs-literal">null</span>, <span class="hljs-literal">true</span>);
